import React, {Component} from 'react';
import ReactDOM from 'react-dom';
import './index.scss';
// import App from './App';
import * as serviceWorker from './serviceWorker';
import {
    HashRouter,
    Route,
    Link,
    Switch,
    NavLink
} from 'react-router-dom'

// ---------- Test ----------

class Test extends Component {
    state = {
        data: false
    }

    componentDidMount() {
        fetch(`http://api.nbp.pl/api/exchangerates/tables/A/`, {
            headers : {
                'Accept': 'application/json'
            }
        }).then(r=>{
            if (r.ok === true) {
                return r.json();
            } else {
                throw new Error('Brak sieci')
            }
        }).then(data=>{
            console.log(data[0].rates)
            this.setState({
                data:data[0]
            })
        }).catch(err=>{
            console.log(err)
        })
    }

    render() {
        if (this.state.data === false) {
            return <h2>Do Bydgoszczy będę jeździł, a tu nie będę kupował!</h2>
        }
        return <ul>
            {/*{this.state.data.map(elem=>{*/}
            {/*    return <li>elem.</li>*/}
            {/*})}*/}
        </ul>
    }
}

// ---------- Apka ----------
const App = () => {
    return (
        <>
            <HashRouter>
                <div className='container'>
                    <Navigation/>
                    <Route exact path="/" component={Calculator}/>
                    <Route exact path="/expenseslist" component={ExpensesList}/>
                </div>
            </HashRouter>
        </>
    )
};

// ---------- Nawigacja ----------

class Navigation extends Component {
    render() {
        return (
            <header className='main'>
                <div className="logo">
                    <i className="fas fa-coins"/>
                    <h1>Kalkulator</h1>
                </div>
                <div className="links">
                    <NavLink exact to="/">
                        <div className="navButton navCalc"><i className="fas fa-calculator"/></div>
                    </NavLink>
                    <NavLink exact to="/ExpensesList">
                        <div className="navButton navList"><i className="fas fa-list-alt"/></div>
                    </NavLink>
                </div>
            </header>
        )
    }
}

// ---------- Kalkulator ----------


class Calculator extends Component {





    render() {
        return (
            <div className='main calculator'>
                <DropdownButton/>
                <input className='amount' placeholder='Wprowadź kwotę' type="number"/>
                <h1>Something</h1>
                <button className='add'>Dodaj</button>
            </div>
        )
    }
}

class DropdownButton extends Component {

    render() {
        return (
            <>
                <div onClick={this.handleChooseCurrency} className="dropdown-button">
                    <h3 >Wybierz walutę</h3>
                </div>
                <DropdownContent/>
            </>
        )
    }
}

class DropdownContent extends Component{

    render() {
        return (
            <div className='dropdown-content'>
                <option value="THB">bat (Tajlandia)</option>
                <option value="USD">dolar amerykański</option>
                <option value="AUD">dolar australijski</option>
                <option value="HKD">dolar Hongkongu</option>
                <option value="CAD">dolar kanadyjski</option>
                <option value="NZD">dolar nowozelandzki</option>
                <option value="SGD">dolar singapurski</option>
                <option value="EUR">euro</option>
                <option value="HUF">forint (Węgry)</option>
                <option value="CHF">frank szwajcarski</option>
                <option value="GBP">funt szterling</option>
                <option value="UAH">hrywna (Ukraina)</option>
                <option value="JPY">jen (Japonia)</option>
                <option value="CZK">korona czeska</option>
                <option value="DKK">korona duńska</option>
                <option value="ISK">korona islandzka</option>
                <option value="NOK">korona norweska</option>
                <option value="SEK">korona szwedzka</option>
                <option value="HRK">kuna (Chorwacja)</option>
                <option value="RON">lej rumuński</option>
                <option value="BGN">lew (Bułgaria)</option>
                <option value="TRY">lira turecka</option>
                <option value="ILS">nowy izraelski szekel</option>
                <option value="CLP">peso chilijskie</option>
                <option value="PHP">peso filipińskie</option>
                <option value="MXN">peso meksykańskie</option>
                <option value="ZAR">rand (Republika Południowej Afryki)</option>
                <option value="BRL">real (Brazylia)</option>
                <option value="MYR">ringgit (Malezja)</option>
                <option value="RUB">rubel rosyjski</option>
                <option value="IDR">rupia indonezyjska</option>
                <option value="INR">rupia indyjska</option>
                <option value="KRW">won południowokoreański</option>
                <option value="CNY">yuan renminbi (Chiny)</option>
            </div>
        )
    }
}


// ---------- Lista wydatków ----------

class ExpensesList extends Component {
    render() {
        return (
            <>
                <h1>Tu będzie lista wydatków</h1>
            </>
        )
    }
}





ReactDOM.render(
    <>
        <Test/>
        <App />
    </>, document.getElementById('root'));

serviceWorker.unregister();
