import React, {Component} from 'react';
import ReactDOM from 'react-dom';
import './index.scss';
import {
    HashRouter,
    Route,
    // Link,
    // Switch,
    NavLink
} from 'react-router-dom'

// ---------- Apka ----------
class App extends Component{

    render() {
        return (
            <>
                <HashRouter>
                    <div className='container'>
                        <Navigation/>
                        <Route exact path="/" component={Calculator}/>
                        <Route exact path="/expenseslist" component={ExpensesList}/>
                    </div>
                </HashRouter>
            </>
        )
    }
};

// ---------- Nawigacja ----------

class Navigation extends Component {
    render() {
        return (
            <header className='main'>
                <div className="logo">
                    <i className="fas fa-coins"/>
                    <h1>Kalkulator</h1>
                </div>
                <div className="links">
                    <NavLink exact to="/">
                        <div className="navButton navCalc"><i className="fas fa-calculator"/></div>
                    </NavLink>
                    <NavLink exact to="/ExpensesList">
                        <div className="navButton navList"><i className="fas fa-list-alt"/></div>
                    </NavLink>
                </div>
            </header>
        )
    }
}

// ---------- Kalkulator ----------


class Calculator extends Component {
    constructor (props) {
        super(props);
        this.state = {
            dropdownIsOpen: false,
            dropdownIsClosed: false,
            selectedRate: '',
            dataFetched: false,
            data: '',
            buttonName: 'Wybierz walutę',
            mid: null,
            amount: null,
            exchangedAmount: null
        };
    }
    componentDidMount() {
        fetch(`http://api.nbp.pl/api/exchangerates/tables/A/`, {
            headers: {
                'Accept': 'application/json'
            }
        }).then(r => {
            if (r.ok === true) {
                return r.json();
            } else {
                throw new Error('Brak sieci')
            }
        }).then(data => {
            // console.log(data[0].rates)
            this.setState({
                data: data[0].rates
            })
        }).catch(err => {
            console.log(err)
        })
    }

    handleOpenDropdown = () => {
        this.setState({dropdownIsOpen: !this.state.dropdownIsOpen})
    };

    handleCloseDropdown = (e) => {
        let getMid;
        let getCurrency;
        this.state.data.filter(r => {
            if (r.code === e.target.value) {
                getMid = r.mid;
                getCurrency = r.currency;
            }
        })
        this.setState({
            dropdownIsOpen: false,
            buttonName: getCurrency,
            mid: getMid
        })

        console.log(getMid);
    }

    handleAmount = (e) => {
        this.setState( {
            amount: e.target.value
        })
        console.log(this.state.amount)
    }

    handleExchangedAmount = (e) => {
        this.setState( {
            exchangedAmount: e.target.value
        })
        console.log(this.state.amount)
    }

    render() {
        if (this.state.dropdownIsOpen === false) {
            return (
                <div className='main calculator'>
                    <DropdownButton onClick={this.handleOpenDropdown} name={this.state.buttonName}/>
                    <Input onChange={this.handleAmount}/>
                    <Exchanged mid={this.state.mid} amount={this.state.amount}/>
                    <Button/>
                </div>
            )
        }
        return (
            <div className='main calculator'>
                <DropdownButton onClick={this.handleOpenDropdown} name={this.state.buttonName}/>
                <DropdownContent onClick={this.handleCloseDropdown} rates={this.state.data}/>
                <Input onChange={this.handleAmount} name={this.state.amount} value={this.state.amount}/>
                <Exchanged/>
                <Button/>
            </div>
        )
    }
}

class DropdownButton extends Component {

    render() {
        return (
            <>
                <div onClick={this.props.onClick} className="dropdown-button">
                    <h3 >{this.props.name}</h3>
                </div>
            </>
        )
    }
}

class DropdownContent extends Component{

    render() {
        return (
            <div className='dropdown-content'>
                {this.props.rates.map((rate)=>{
                    return <option onClick={this.props.onClick} key={rate.code} value={rate.code}>{rate.currency}</option>
                })}
            </div>
        )
    }
}

class Input extends Component {
    render() {
        return <input onChange={this.props.onChange}
                      name={this.props.value}
                      value={this.props.amount}
                      className='amountInput'
                      placeholder='Wprowadź kwotę'
                      type="number"/>
    }
}

class Exchanged extends Component {
    render() {
        return <div className="exchanged"><h1 className='exchangedText'>{(this.props.mid * this.props.amount).toFixed(2)}</h1></div>
    }
}

class Button extends Component {
    render() {
        return <button className='addBtn'>Dodaj</button>
    }
}


// ---------- Lista wydatków ----------

class ExpensesList extends Component {
    render() {
        return (
            <>
                <h1>Tu będzie lista wydatków</h1>
            </>
        )
    }
}

ReactDOM.render(
    <>
        <App />
    </>, document.getElementById('root'));
